package githubdemo;

import java.util.Scanner;

public class add {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b,sum;
		System.out.println("Enter two numbers");
		Scanner scan=new Scanner(System.in);
		a=scan.nextInt();
		b=scan.nextInt();
		sum=a+b;
		System.out.println("The sum is : "+sum);

	}

}
