package githubdemo;

import java.util.Scanner;

public class sub {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b,sub;
		System.out.println("Enter two numbers");
		Scanner scan=new Scanner(System.in);
		a=scan.nextInt();
		b=scan.nextInt();
		sub=a-b;
		System.out.println("The sum is : "+sub);

	}

}
